sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    if(gs.getUserID()!=null){
      	var user=gs.getUser();
        var roles=gs.getUser().roles;
        if(roles&&(roles.contains("x_yunji_jet_04.BOSS"))){
            var encoded_query = current.getEncodedQuery() + '';
          	//gs.print('encoded_query test:' + encoded_query)

            if (encoded_query != null && encoded_query.includes('employeeISNOTEMPTY')){
                current.addDisplayQuery("employee",user.getFirstName()+user.getLastName());
                //gs.print('encoded_query test:' + current.getEncodedQuery());
            }
        }else{
            current.addDisplayQuery("employee",user.getFirstName()+user.getLastName())
        }
    }

})(current, previous);
