sys_ui_action.script:
function validateSalaryDetail() {
  var source_record_id = g_form.getValue('document_id');
  var ga = new GlideAjax('ValidateSalaryDetailService');
  ga.addParam('sysparm_name', 'validateResult');
  ga.addParam('salaryApprovalId', source_record_id);
  ga.getJson(response => {
    debugger;
    if(response.data == null || response.status != "success"){
      const msg = new GwtMessage();
      msg.addErrorMessage('薪资数据校验异常');
      return;
    }
    var empNum = response.data.result.empNum;
    var attendanceNum = response.data.result.attendanceNum;
    var salaryNum = response.data.result.salaryNum;
    var salaryNum = response.data.result.salaryNum;
    var socialSecurityNum = response.data.result.socialSecurityNum;
    var onLineEmpNotAttendance = response.data.result.onLineEmpNotAttendance;
    var onLineEmpNotSalary = response.data.result.onLineEmpNotSalary;

    var info = {};
    var flg = true;
    if (empNum == 0) {
      flg = false;
      info.员工数量为 = "0!"
    }
    if (attendanceNum == 0) {
      flg = false;
      info.考勤记录为 = "0!"
    }
    if (salaryNum == 0) {
      flg = false;
      info.薪资记录为 = "0!"
    }
    if (socialSecurityNum == 0) {
      flg = false;
      info.社保记录为 = "0!"
    }
    if (onLineEmpNotAttendance != 0 && flg) {
      info.在职员工缺少考勤记录数量 = onLineEmpNotAttendance + "!"
    }
    if (onLineEmpNotSalary != 0 && flg) {
      info.在职员工缺少薪资记录数量 = onLineEmpNotSalary + "!"
    }
    debugger;
    if (checkNullObj(info)) {
      //校验错误信息为空,审批同意
      /** const sys_id = g_form.getUniqueValue();
       const tableName = g_form.getTableName();
       const dataMap = {state: 'approved'};
       ajaxHelper = new GlideAjax('UpdateApprovalAjax');
       ajaxHelper.addParam('sysparm_obj_id', sys_id);
       ajaxHelper.addParam('sysparm_table_name', tableName);
       ajaxHelper.addParam('dataMap', dataMap);
       const exec = (click) => {
        click()
      }
      ajaxHelper.getJsonAnswer(exec.bind(this)); */
      debugger;

    } else {
      //校验异常显示异常信息
      var dlg = new GlideModal();
      dlg.setTitle(`校验结果异常`);
      dlg.setPreferences(info);
      dlg.render();
    }
  });
}


function checkNullObj(obj) {
  return Object.keys(obj).length === 0
}
